import React, { Component } from "react";
import "./App.css";
import Person from "./Person/Person";
class App extends Component {
  state = {
    persons: [
      { name: "max", age: 24 },
      { name: "john", age: 14 },
      { name: "ana", age: 32 },
    ],
  };

  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: newName, age: 24 },
        { name: "john", age: 14 },
        { name: "ana", age: 32 },
      ],
    });
  };
  render() {
    return (
      <div className="App">
        <h2>HI THERE</h2>
        <p>A good paragraph</p>
        <button onClick={this.switchNameHandler.bind(this, "Jorgito")}>
          Switch name
        </button>
        <Person
          name={this.state.persons[0].name}
          age={this.state.persons[0].age}
        />
        <Person
          name={this.state.persons[1].name}
          age={this.state.persons[1].age}
          click={this.switchNameHandler}
        >
          My hobbies: Racing bla bla
        </Person>
        <Person
          name={this.state.persons[2].name}
          age={this.state.persons[2].age}
        />
      </div>
    );
    // return React.createElement(
    //   "div",
    //   { className: "App" },
    //   React.createElement("h1", null, "hi im nesting")
    // );
  }
}

export default App;
