import React, { Component } from "react";
import "./App.css";
import Person from "./Person/Person";
class App extends Component {
  state = {
    persons: [
      { name: "max", age: 24 },
      { name: "john", age: 14 },
      { name: "ana", age: 32 },
    ],
  };

  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: newName, age: 24 },
        { name: "john", age: 14 },
        { name: "ana", age: 32 },
      ],
    });
  };
  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        { name: "Max", age: 24 },
        { name: event.target.value, age: 44 },
        { name: "ana", age: 32 },
      ],
    });
  };
  render() {
    const style = {
      backgroundColor: "white",
      font: "inherit",
      border: "1px solid blue",
      padding: "8px",
      cursor: "pointer",
    };
    return (
      <div className="App">
        <h2>HI THERE</h2>
        <p>A good paragraph</p>
        <button
          style={style}
          onClick={this.switchNameHandler.bind(this, "Jorgito")}
        >
          Switch name
        </button>
        <div>
          <Person
            name={this.state.persons[0].name}
            age={this.state.persons[0].age}
          />
          <Person
            name={this.state.persons[1].name}
            age={this.state.persons[1].age}
            click={() => this.switchNameHandler("Jorge!!")}
            changed={this.nameChangeHandler}
          >
            My hobbies: Racing bla bla
          </Person>
          <Person
            name={this.state.persons[2].name}
            age={this.state.persons[2].age}
          />
        </div>
      </div>
    );
    // return React.createElement(
    //   "div",
    //   { className: "App" },
    //   React.createElement("h1", null, "hi im nesting")
    // );
  }
}

export default App;
